INSERT INTO `task` VALUES (307, 34, '22', '&lt;p&gt;&lt;a target=&quot;_blank&quot; href=&quot;javascript:loadKnowledgepointParagraph(108)&quot; style=&quot;white-space: normal; color: rgb(19, 110, 194); text-decoration-line: none; font-family: arial, 宋体, sans-serif; text-indent: 28px; background-color: rgb(255, 255, 255);&quot;&gt;活锁&lt;/a&gt;&lt;/p&gt;&lt;p&gt;如果事务T&lt;a target=&quot;_blank&quot; href=&quot;javascript:loadKnowledgepointParagraph(415)&quot; style=&quot;white-space: normal; color: rgb(19, 110, 194); text-decoration-line: none; font-family: arial, 宋体, sans-serif; text-indent: 28px; background-color: rgb(255, 255, 255);&quot;&gt;封锁&lt;/a&gt;了数据R，事务T,又请求&lt;a target=&quot;_blank&quot; href=&quot;javascript:loadKnowledgepointParagraph(415)&quot; style=&quot;white-space: normal; color: rgb(19, 110, 194); text-decoration-line: none; font-family: arial, 宋体, sans-serif; text-indent: 28px; background-color: rgb(255, 255, 255);&quot;&gt;封锁&lt;/a&gt;R，于是T2等待:Ts也请求&lt;a target=&quot;_blank&quot; href=&quot;javascript:loadKnowledgepointParagraph(415)&quot; style=&quot;white-space: normal; color: rgb(19, 110, 194); text-decoration-line: none; font-family: arial, 宋体, sans-serif; text-indent: 28px; background-color: rgb(255, 255, 255);&quot;&gt;封锁&lt;/a&gt;R，当T释放了R.上的&lt;a target=&quot;_blank&quot; href=&quot;javascript:loadKnowledgepointParagraph(415)&quot; style=&quot;white-space: normal; color: rgb(19, 110, 194); text-decoration-line: none; font-family: arial, 宋体, sans-serif; text-indent: 28px; background-color: rgb(255, 255, 255);&quot;&gt;封锁&lt;/a&gt;之后系统首先批准了T;的请求，T2仍然等待:然后T.又请求&lt;a target=&quot;_blank&quot; href=&quot;javascript:loadKnowledgepointParagraph(415)&quot; style=&quot;white-space: normal; color: rgb(19, 110, 194); text-decoration-line: none; font-family: arial, 宋体, sans-serif; text-indent: 28px; background-color: rgb(255, 255, 255);&quot;&gt;封锁&lt;/a&gt;R，当T3释放了R上的&lt;a target=&quot;_blank&quot; href=&quot;javascript:loadKnowledgepointParagraph(415)&quot; style=&quot;white-space: normal; color: rgb(19, 110, 194); text-decoration-line: none; font-family: arial, 宋体, sans-serif; text-indent: 28px; background-color: rgb(255, 255, 255);&quot;&gt;封锁&lt;/a&gt;之后系统又批准了T.的请求....有可能永远等待，这就是&lt;a target=&quot;_blank&quot; href=&quot;javascript:loadKnowledgepointParagraph(108)&quot; style=&quot;white-space: normal; color: rgb(19, 110, 194); text-decoration-line: none; font-family: arial, 宋体, sans-serif; text-indent: 28px; background-color: rgb(255, 255, 255);&quot;&gt;活锁&lt;/a&gt;的情形.&lt;/p&gt;', '2018-05-28 18:47:40', '2018-05-28 18:47:39', 1, 'admin', 'admin');
