INSERT INTO `task` VALUES (314, 243, '22', '&lt;p&gt;&lt;span style=&quot;font-size:16px;font-family:宋体&quot;&gt;级&lt;a target=&quot;_blank&quot; href=&quot;javascript:loadKnowledgepointParagraph(415)&quot; style=&quot;white-space: normal; color: rgb(19, 110, 194); text-decoration-line: none; font-family: arial, 宋体, sans-serif; text-indent: 28px; background-color: rgb(255, 255, 255);&quot;&gt;封锁&lt;/a&gt;协议是指，在一级&lt;a target=&quot;_blank&quot; href=&quot;javascript:loadKnowledgepointParagraph(415)&quot; style=&quot;white-space: normal; color: rgb(19, 110, 194); text-decoration-line: none; font-family: arial, 宋体, sans-serif; text-indent: 28px; background-color: rgb(255, 255, 255);&quot;&gt;封锁&lt;/a&gt;协议基础上增加事务T在读取数据R之前必须先对其加S锁，读完后即可释放S锁。&lt;br/&gt; 二级&lt;a target=&quot;_blank&quot; href=&quot;javascript:loadKnowledgepointParagraph(415)&quot; style=&quot;white-space: normal; color: rgb(19, 110, 194); text-decoration-line: none; font-family: arial, 宋体, sans-serif; text-indent: 28px; background-color: rgb(255, 255, 255);&quot;&gt;封锁&lt;/a&gt;协议除防止了丢失修改，还可进一步防止读&ldquo;脏&rdquo;数据。&lt;/span&gt;&lt;/p&gt;', '2018-05-28 19:26:24', '2018-05-28 19:26:22', 1, 'admin', 'admin');
